import jakarta.persistence.*;
import java.math.BigDecimal;
import java.time.LocalDate;

@Entity
@Table(name = "commande")
public class Commande {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    @Column(nullable = false)
    private String description;

    @Column(nullable = false)
    private Integer quantite;

    @Column(nullable = false)
    private LocalDate date;

    @Column(nullable = false, precision = 19, scale = 2)
    private BigDecimal montant;

    public Commande() {
    }

    public Commande(String description, Integer quantite, LocalDate date, BigDecimal montant) {
        this.description = description;
        this.quantite = quantite;
        this.date = date;
        this.montant = montant;
    }

    public Long getId() { return id; }
    public String getDescription() { return description; }
    public Integer getQuantite() { return quantite; }
    public LocalDate getDate() { return date; }
    public BigDecimal getMontant() { return montant; }

    public void setId(Long id) { this.id = id; }
    public void setDescription(String description) { this.description = description; }
    public void setQuantite(Integer quantite) { this.quantite = quantite; }
    public void setDate(LocalDate date) { this.date = date; }
    public void setMontant(BigDecimal montant) { this.montant = montant; }

import ma.emsi.microservicecommandes.domain.Commande;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.stereotype.Repository;

@Repository
public interface CommandeRepository extends JpaRepository<Commande, Long>

import ma.emsi.microservicecommandes.domain.Commande;
import ma.emsi.microservicecommandes.repository.CommandeRepository;
import org.springframework.stereotype.Service;

import java.util.List;

@Service
public class CommandeService {

    private final CommandeRepository commandeRepository;

    public CommandeService(CommandeRepository commandeRepository) {
        this.commandeRepository = commandeRepository;
    }

    public Commande create(Commande commande) {
        return commandeRepository.save(commande);
    }

    public List<Commande> findAll() {
        return commandeRepository.findAll();
    }

    public Commande findById(Long id) {
        return commandeRepository.findById(id)
                .orElseThrow(() -> new RuntimeException("Commande introuvable avec id = " + id));
    }

    public Commande update(Long id, Commande newData) {
        Commande existing = findById(id);
        existing.setDescription(newData.getDescription());
        existing.setQuantite(newData.getQuantite());
        existing.setDate(newData.getDate());
        existing.setMontant(newData.getMontant());
        return commandeRepository.save(existing);
    }

    public void delete(Long id) {
        if (!commandeRepository.existsById(id)) {
            throw new RuntimeException("Commande introuvable avec id = " + id);
        }
        commandeRepository.deleteById(id);
    }

import ma.emsi.microservicecommandes.domain.Commande;
import ma.emsi.microservicecommandes.service.CommandeService;
import org.springframework.http.HttpStatus;
import org.springframework.web.bind.annotation.*;

import java.util.List;

@RestController
@RequestMapping("/api/commandes")
public class CommandeController {

    private final CommandeService commandeService;

    public CommandeController(CommandeService commandeService) {
        this.commandeService = commandeService;
    }

    // CREATE
    @PostMapping
    @ResponseStatus(HttpStatus.CREATED)
    public Commande create(@RequestBody Commande commande) {
        return commandeService.create(commande);
    }

    // READ ALL
    @GetMapping
    public List<Commande> findAll() {
        return commandeService.findAll();
    }

    // READ BY ID
    @GetMapping("/{id}")
    public Commande findById(@PathVariable Long id) {
        return commandeService.findById(id);
    }

    // UPDATE
    @PutMapping("/{id}")
    public Commande update(@PathVariable Long id, @RequestBody Commande commande) {
        return commandeService.update(id, commande);
    }

    // DELETE
    @DeleteMapping("/{id}")
    @ResponseStatus(HttpStatus.NO_CONTENT)
    public void delete(@PathVariable Long id) {
        commandeService.delete(id);
    }

